INSERT INTO `pt_posts` VALUES ('196', '1', '2017-06-04 00:59:07', '2017-06-04 00:59:07', '新西兰画笔艺术学校，是一所新西兰公认的最专业，最优秀的具有合法资质的艺术学校，拥有专业的师资力量，科学、正规的美术学院教学方法和艺术氛围浓厚的学习环境。在这里，您的孩子能够得到最专业最优秀且最人性化的教育。\r\n\r\n经过认真尽力的精心选址，备课以及准备教具，新西兰画笔艺术学校的第二个校址：北岸分校，已经于2017年6月4日正式开课。\r\n\r\n招生面向所有少年儿童直至成人，课程包含专业绘画课程、语言艺术，专业动漫，少儿启蒙绘画，青少年书法国画，多媒体设计与影视制作等，无论你的基础如何，也无论你的年龄大小，只要你有握住画笔，握住艺术的那颗心，我们都诚挚的欢迎你的加入。\r\n\r\n我们拥有宽敞明亮的教学环境，专业的器材设备。\r\n\r\n<img class=\"aligncenter wp-image-206 size-full\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1161541811402_.pic_.jpg\" alt=\"\" width=\"640\" height=\"660\" />\r\n\r\n<img class=\"aligncenter wp-image-197 size-full\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1061541811100_.pic_.jpg\" alt=\"\" width=\"640\" height=\"427\" />\r\n\r\n<img class=\"alignleft wp-image-202 size-full\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1111541811137_.pic_.jpg\" alt=\"\" width=\"640\" height=\"427\" /> <img class=\"size-medium wp-image-200 alignleft\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1091541811127_.pic_-200x300.jpg\" alt=\"\" /> <img class=\"size-medium wp-image-199 alignleft\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1081541811121_.pic_-200x300.jpg\" alt=\"\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<section class=\"\"><section class=\"\"><section class=\"\">同时在第一天开课的日子里学校还精心准备了点心迎接各位同学的到来。<img class=\"alignleft size-full wp-image-204\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1141541811217_.pic_.jpg\" alt=\"\" />\r\n\r\n</section>&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n同学们在崭新教室上课。\r\n\r\n<img class=\"alignleft size-full wp-image-203\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1131541811211_.pic_.jpg\" alt=\"\" /> <img class=\"alignleft size-full wp-image-205\" src=\"http://paintingpenart.school.nz/wp-content/uploads/2018/11/1151541811222_.pic_.jpg\" alt=\"\" />\r\n\r\n</section></section>', '新西兰画笔艺术学校北岸分校正式开课', '', 'publish', 'closed', 'closed', '', '%e3%80%902017%e5%b9%b46%e6%9c%88%e3%80%91%e6%96%b0%e8%a5%bf%e5%85%b0%e7%94%bb%e7%ac%94%e8%89%ba%e6%9c%af%e5%ad%a6%e6%a0%a1%e5%8c%97%e5%b2%b8%e5%88%86%e6%a0%a1%e6%ad%a3%e5%bc%8f%e5%bc%80%e8%af%be', '', '', '2018-11-10 02:08:25', '2018-11-10 02:08:25', '', '0', 'http://paintingpenart.school.nz/?p=196', '0', 'post', '', '0');
